rule"Evaluation - Self-Monitoring Parser Graylog 1"

when
    contains(to_string($message.image_name),"graylog")
    //true

then
    let mylog = grok(
        value: to_string($message.message),
        pattern: "%{TIMESTAMP_ISO8601}%{SPACE}%{WORD:event_severity}%{SPACE}:%{SPACE}%{NOTSPACE:application_component}%{SPACE}-%{SPACE}%{GREEDYDATA:message}"
    );

    set_fields(mylog);
    remove_multiple_fields(
        pattern: "[A-Z0-9_-]+"
    );
    //set_field("touched",true);

end

rule"Evaluation - Self-Monitoring Parser Opensearch 1"

when
    contains(to_string($message.image_name),"opensearch")
    //true

then
    let mylog = grok(
        value: to_string($message.message),
        pattern: "\\[%{TIMESTAMP_ISO8601}\\]%{SPACE}\\[%{WORD:event_severity}%{SPACE}\\]%{SPACE}\\[%{SPACE}%{GREEDYDATA:application_component}%{SPACE}\\]%{SPACE}\\[%{SPACE}%{HOSTNAME:event_source}%{SPACE}\\]%{GREEDYDATA:message}"
    );

    set_fields(mylog);
    remove_multiple_fields(
        pattern: "[A-Z0-9_-]+"
    );
    //set_field("touched",true);
end

rule"Evaluation - Self-Monitoring Parser MongoDB 1"

when
    contains(to_string($message.image_name),"mongo")
    //true

then
    let mylog = to_map(parse_json(to_string(flatten_json(to_string($message.message),"flatten"))));

    set_fields(mylog);

    rename_field("attr_message_msg","message");
    rename_field("attr_message_verbose_level","event_severity");
    rename_field("attr_message_session_name","application_component");
    //set_field("touched",true);

end

