rule"Evaluation - Self-Monitoring Parser Opensearch 1"

when
    contains(to_string($message.image_name),"opensearch")

then
    let mylog = grok(
        value: to_string($message.message),
        pattern: "\\[%{TIMESTAMP_ISO8601:event_created}\\]%{SPACE}\\[%{WORD:event_severity}%{SPACE}\\]%{SPACE}\\[%{SPACE}%{GREEDYDATA:application_component}%{SPACE}\\]%{SPACE}\\[%{SPACE}%{HOSTNAME:event_source}%{SPACE}\\]%{GREEDYDATA:message}"
    );

    set_fields(mylog);

    rename_field(
        old_field: "created",
        new_field: "container_created"
    );

    remove_multiple_fields(
        pattern: "[A-Z0-9_-]+",
        names: ["container_id","image_id","tag"]
    );
end

rule"Evaluation - Self-Monitoring Parser Graylog 1"

when
    contains(to_string($message.image_name),"graylog")
    //true

then
    let mylog = grok(
        value: to_string($message.message),
        pattern: "%{TIMESTAMP_ISO8601:event_created}%{SPACE}%{WORD:event_severity}%{SPACE}:%{SPACE}%{NOTSPACE:application_component}%{SPACE}-%{SPACE}%{GREEDYDATA:message}"
    );

    set_fields(mylog);

    rename_field(
        old_field: "created",
        new_field: "container_created"
    );

    remove_multiple_fields(
        pattern: "[A-Z0-9_-]+",
        names: ["container_id","image_id","tag"]
    );

end

rule"Evaluation - Self-Monitoring Parser Graylog Installer"

when
    contains(to_string($message.message),"GRAYLOG-") 
    //true

then
    let mylog = grok(
        value: to_string($message.message),
        pattern: "%{TIMESTAMP_ISO8601}%{SPACE}%{HOSTNAME:event_source}%{SPACE}%{DATA:application_component}:%{SPACE}\\[%{WORD:event_severity}\\]%{SPACE}-%{SPACE}%{GREEDYDATA:message}"
    );

    set_fields(mylog);
    remove_multiple_fields(
        pattern: "[A-Z0-9_-]+"
    );
    //set_field("touched",true);

end

rule"Evaluation - Self-Monitoring Parser MongoDB 1"

when
    contains(to_string($message.image_name),"mongo")
    //true
then

    let mylog = flatten_json(to_string(parse_json(replace(to_string($message.message),"$"))),"flatten",true);

    set_fields(to_map(mylog));

    rename_field("attr_message_msg","message");
    rename_field("attr_message_verbose_level","event_severity");
    rename_field("attr_message_session_name","application_component");
    rename_field("t_date","event_created");
    rename_field("created","container_created");

    remove_multiple_fields(
        pattern: "attr_[A-Za-z-_]*",
        names: ["c","container_id","ctx","id","image_id","msg","s","tag"]
    );
end
